   WHERE G.mmed_tipo = 1
   GROUP BY A.eatv_referencia, E.greg_id, D.uneg_id, D.muni_id, D.loca_id, B.eeat_id) AS B ON 
  A.greg_id = B.greg_id AND 
  A.uneg_id = B.uneg_id AND 
  A.muni_id = B.muni_id AND 
