--

CREATE FUNCTION resto11(character varying) RETURNS integer
    LANGUAGE plpgsql
    AS $_$
/* New function body */
DECLARE

--Parametro
pString alias for $1; /* $1 refere-se ao parametro Varchar */

--Variaveis
v_Soma int4;
v_Reg int4;
v_Multi int4;
v_Resto int4;
v_I int4;
v_J int4;
v_H int4;

BEGIN
v_Soma := 0;
v_Reg := 0;
v_Multi := 2;
v_I:= length(pString);
v_H := v_I;
v_J := 1;

while (v_J <= v_I) loop
begin
v_Reg := cast(substr(pString,v_H,1) as int4)*v_Multi;
v_Soma:=v_Soma+v_Reg;
if(v_Multi = 9) then
v_Multi :=2;
else
v_Multi := v_Multi+1;
end if;
v_J:= v_J+1;
v_H:= v_H-1;
end;
end loop;
v_Resto:= mod(v_Soma,11);
v_Resto:=11-v_Resto;
--if(v_Resto>=10) or (v_Resto=1) or (v_Resto=0) then 
--if (v_Resto=1) or (v_Resto=0) then 
--RETURN 0;
--else
RETURN v_Resto;
--end if;
END;
$_$;


