--
-- Name: vw_db_backup; Type: VIEW; Schema: admindb; Owner: gsan_admin
--

CREATE VIEW vw_db_backup AS
 SELECT db_backup.dbbk_nmdatabase AS "Nome da Base",
        CASE
            WHEN (db_backup.dbbk_icbackup = 1) THEN 'Backup Logico'::text
            WHEN (db_backup.dbbk_icbackup = 2) THEN 'Backup Fisico'::text
            ELSE 'Nao Definido'::text
        END AS "Tipo do Backup",
    db_backup.dbbk_dsbackup AS "Descrição",
    db_backup.dbbk_tminicio AS "Data/Hora Inicio",
    db_backup.dbbk_tmtermino AS "Data/Hora Fim",
    db_backup.dbbk_hrduracao AS "Duração",
    db_backup.dbbk_nntamdatabase AS "Tamanho da Base",
    db_backup.dbbk_nntamarqbackup AS "Tam. Arq. Dump",
    db_backup.dbdk_nmlogindba AS "Nome do Dba",
        CASE
            WHEN (db_backup.dbbk_ictermino = 1) THEN 'Normal'::text
            ELSE 'Com Anormalidade'::text
        END AS "Término",
    db_backup.dbbk_dsobservacao AS "Observação/Ocorrencia"
   FROM db_backup
  ORDER BY db_backup.dbbk_tminicio DESC;


ALTER TABLE vw_db_backup OWNER TO gsan_admin;

--
