    db_vacuum.dbva_dsvacuum AS "Descrição",
    db_vacuum.dbva_tminicio AS "Data/Hora Inicio",
    db_vacuum.dbva_tmtermino AS "Data/Hora Fim",
    db_vacuum.dbva_hrduracao AS "Duração",
    db_vacuum.dbva_nntamdatabase AS "Tamanho da Base",
    db_vacuum.dbva_nmlogindba AS "Nome do Dba",
        CASE
