-- Name: geraindicador(date); Type: FUNCTION; Schema: operacao; Owner: gsan_admin
--

CREATE FUNCTION geraindicador(mesreferencia date) RETURNS void
    LANGUAGE plpgsql
    AS $_$
DECLARE
  dataInicial date := mesReferencia;
  dataFinal date := operacao.last_day(mesReferencia);
  diasMes integer := operacao.dias_nummes(extract(month from mesReferencia), extract(year from mesReferencia));
  referenciaAnoMes integer := extract(year from mesReferencia) * 100 + extract(month from mesReferencia);  

  rowcount integer;
BEGIN
  RAISE NOTICE 'Chamada da funcao de geracao de indicadores';
  RAISE NOTICE 'Data de referencia: (%) ', dataInicial;
  RAISE NOTICE 'AnoMesReferencia: (%) ', referenciaAnoMes;
  
  DELETE FROM operacao.indicador_mensal
   WHERE indm_data = dataInicial;

  --INDICADOR 1 - Consumo Produtos Químicos
  INSERT INTO operacao.indicador_mensal 
  SELECT A.referencia, 1 AS indicador, A.greg_id, A.uneg_id, A.muni_id, A.loca_id, A.unop_tipooperacional, A.unop_id,
         operacao.ISNULL(CAST(B.consumo_produto AS NUMERIC), 0.0), 
         operacao.ISNULL(CAST(A.volume_agua AS NUMERIC), 0.0), 
         (CASE WHEN A.volume_agua = 0 THEN 0 ELSE (operacao.ISNULL(CAST(B.consumo_produto AS NUMERIC), 0.0)::numeric / A.volume_agua::numeric) * 100 END)
    FROM (
  SELECT A.referencia, E.greg_id, D.uneg_id, D.muni_id, D.loca_id, 2 AS unop_tipooperacional, B.eta_id AS unop_id, SUM(F.volume) AS volume_agua
    FROM operacao.volume A
   INNER JOIN operacao.volume_fluxo F ON A.id = F.volume_id AND F.tipofluxo = 2
   INNER JOIN operacao.eta B ON A.id_unidade = B.eta_id 
   INNER JOIN operacao.unidade_consumidora_operacional C ON B.eta_id = C.ucop_idoperacional AND C.ucop_tipooperacional = 2
   INNER JOIN operacao.unidade_consumidora D ON C.ucon_id = D.ucon_id
   INNER JOIN cadastro.unidade_negocio E ON D.uneg_id = E.uneg_id
   WHERE A.tipo_unidade = 2 AND A.referencia = dataInicial
   GROUP BY A.referencia, E.greg_id, D.uneg_id, D.muni_id, D.loca_id, B.eta_id) AS A
  LEFT JOIN (
  select uc.greg_id, uc.uneg_id, uc.muni_id, uc.loca_id, c.tipo_unidade as unop_tipooperacional, c.id_unidade as unop_id, SUM(cp.quantidade) AS consumo_produto
  from operacao.consumo c
  inner join operacao.consumo_produto cp on
    cp.cons_id = c.id
  inner join operacao.unidade_consumidora_operacional ucop on
    ucop.ucop_tipooperacional = c.tipo_unidade
    and ucop.ucop_idoperacional = c.id_unidade
  inner join operacao.unidade_consumidora uc on
    uc.ucon_id = ucop.ucon_id
  WHERE c.data BETWEEN dataInicial AND dataFinal 
  group by uc.greg_id, uc.uneg_id, uc.muni_id, uc.loca_id, c.tipo_unidade, c.id_unidade) AS B ON A.unop_tipooperacional = B.unop_tipooperacional AND A.unop_id = B.unop_id;   

  --INDICADOR 2 - Produtividade do Capital em Produto Químico
  INSERT INTO operacao.indicador_mensal
  SELECT A.referencia, 2 AS indicador, A.greg_id, A.uneg_id, A.muni_id, A.loca_id, A.unop_tipooperacional, A.unop_id,
         operacao.ISNULL(CAST(A.volume_agua AS NUMERIC), 0.0), 
         operacao.ISNULL(CAST(B.consumo_produto AS NUMERIC), 0.0), 
         (CASE WHEN operacao.ISNULL(CAST(B.consumo_produto AS NUMERIC), 0.0) = 0 THEN 0 ELSE (A.volume_agua::numeric / operacao.ISNULL(CAST(B.consumo_produto AS NUMERIC), 0.0)::numeric) * 100 END)
    FROM (
   SELECT A.referencia, E.greg_id, D.uneg_id, D.muni_id, D.loca_id, 2 AS unop_tipooperacional, B.eta_id AS unop_id, SUM(F.volume) AS volume_agua
   FROM operacao.volume A
   INNER JOIN operacao.volume_fluxo F ON A.id = F.volume_id AND F.tipofluxo = 2
   INNER JOIN operacao.eta B ON A.id_unidade = B.eta_id 
   INNER JOIN operacao.unidade_consumidora_operacional C ON B.eta_id = C.ucop_idoperacional AND C.ucop_tipooperacional = 2
   INNER JOIN operacao.unidade_consumidora D ON C.ucon_id = D.ucon_id
   INNER JOIN cadastro.unidade_negocio E ON D.uneg_id = E.uneg_id
   WHERE A.tipo_unidade = 2 AND A.referencia = dataInicial 
   GROUP BY A.referencia, E.greg_id, D.uneg_id, D.muni_id, D.loca_id, B.eta_id) AS A
  LEFT JOIN (
  select uc.greg_id, uc.uneg_id, uc.muni_id, uc.loca_id, c.tipo_unidade as unop_tipooperacional, c.id_unidade as unop_id, SUM(cp.quantidade * (SELECT preco FROM operacao.preco_produto X WHERE X.vigencia <= c.data AND X.prod_id = cp.prod_id ORDER BY X.vigencia DESC LIMIT 1)) AS consumo_produto
  from operacao.consumo c
  inner join operacao.consumo_produto cp on
    cp.cons_id = c.id
  inner join operacao.unidade_consumidora_operacional ucop on
    ucop.ucop_tipooperacional = c.tipo_unidade
    and ucop.ucop_idoperacional = c.id_unidade
  inner join operacao.unidade_consumidora uc on
    uc.ucon_id = ucop.ucon_id
  WHERE c.data BETWEEN dataInicial AND dataFinal 
  group by uc.greg_id, uc.uneg_id, uc.muni_id, uc.loca_id, c.tipo_unidade, c.id_unidade) AS B ON A.unop_tipooperacional = B.unop_tipooperacional AND A.unop_id = B.unop_id;

  --INDICADOR 3 - Produtividade do Capital em Energia Elétrica
  --volume total de água tratada / gasto total com produto qumico na produção
  INSERT INTO operacao.indicador_mensal
  SELECT A.referencia, 3 AS indicador, A.greg_id, A.uneg_id, A.muni_id, A.loca_id, A.unop_tipooperacional, A.unop_id, 
         operacao.ISNULL(CAST(A.volume_agua AS NUMERIC), 0.0), 
         operacao.ISNULL(CAST(B.valor_energia AS NUMERIC), 0.0), 
         (CASE WHEN operacao.ISNULL(CAST(B.valor_energia AS NUMERIC), 0.0) = 0 THEN 0 ELSE (operacao.ISNULL(CAST(A.volume_agua AS NUMERIC), 0.0) / B.valor_energia::numeric) * 100 END)
    FROM (
  SELECT A.referencia, E.greg_id, D.uneg_id, D.muni_id, D.loca_id, 2 AS unop_tipooperacional, B.eta_id AS unop_id, SUM(F.volume) AS volume_agua
    FROM operacao.volume A
   INNER JOIN operacao.volume_fluxo F ON A.id = F.volume_id AND F.tipofluxo = 2
   INNER JOIN operacao.eta B ON A.id_unidade = B.eta_id    
   INNER JOIN operacao.unidade_consumidora_operacional C ON B.eta_id = C.ucop_idoperacional AND C.ucop_tipooperacional = 2
   INNER JOIN operacao.unidade_consumidora D ON C.ucon_id = D.ucon_id
   INNER JOIN cadastro.unidade_negocio E ON D.uneg_id = E.uneg_id
   WHERE A.tipo_unidade = 2 AND A.referencia = dataInicial
   GROUP BY A.referencia, E.greg_id, D.uneg_id, D.muni_id, D.loca_id, B.eta_id) AS A
  LEFT JOIN (
  SELECT D.greg_id, C.uneg_id, C.muni_id, C.loca_id, ucop_tipooperacional as unop_tipooperacional, ucop_idoperacional AS unop_id, enld_vlr_TotalP * ucop_percentual / 100 AS valor_energia
    FROM operacao.energiaeletrica A
   INNER JOIN operacao.energiaeletrica_dados B ON A.enel_id = B.enel_id
   INNER JOIN operacao.unidade_consumidora C ON B.enld_uc = C.ucon_uc
   INNER JOIN cadastro.unidade_negocio D ON C.uneg_id = D.uneg_id
   INNER JOIN operacao.unidade_consumidora_operacional E ON C.ucon_id = E.ucon_id
   WHERE A.enel_referencia = referenciaAnoMes
   ) AS B ON A.greg_id = B.greg_id AND A.uneg_id = B.uneg_id AND A.muni_id = B.muni_id AND A.loca_id = B.loca_id AND A.unop_tipooperacional = B.unop_tipooperacional AND A.unop_id = B.unop_id;

  --INDICADOR 4 - Horas Paradas nos Sistemas
  INSERT INTO operacao.indicador_mensal
  SELECT DISTINCT dataInicial AS referencia, 4 AS indicador, B.greg_id, A.uneg_id, A.muni_id, A.loca_id, ucop_tipooperacional as unop_tipooperacional, ucop_idoperacional AS unop_id, 
         operacao.ISNULL(CAST(total_horas - total_trabalhado AS NUMERIC), CAST(0 AS NUMERIC)) AS total_parado, operacao.ISNULL(total_horas, 0),
             (case operacao.ISNULL(total_horas, 0) WHEN 0 THEN 0 ELSE (operacao.ISNULL(total_horas - operacao.ISNULL(CAST(total_horas - total_trabalhado AS NUMERIC), CAST(0 AS NUMERIC)),0) / total_horas) * 100 END) AS indicador
    FROM operacao.unidade_consumidora A
   INNER JOIN cadastro.unidade_negocio B ON A.uneg_id = B.uneg_id
   INNER JOIN operacao.unidade_consumidora_operacional C ON A.ucon_id = C.ucon_id
   LEFT JOIN ( SELECT tipo, id, quantidade_cmb * 24 * diasMes as total_horas FROM operacao.estacao_operacional ) AS esta ON
     esta.id = C.ucop_idoperacional and esta.tipo = C.ucop_tipooperacional
   LEFT JOIN ( SELECT hora_tipo_unidade as tipo_unidade, hora_id_unidade as id_unidade, sum(hcmb_medicao * hcmb_qtd) as total_trabalhado
   	    FROM operacao.hora_cmb cmb
        inner join operacao.hora hora on
        cmb.hora_id = hora.hora_id
        where hora.hora_ano_mes_referencia = referenciaAnoMes
        GROUP BY hora.hora_tipo_unidade, hora.hora_id_unidade ) AS cmb on 
          cmb.tipo_unidade = C.ucop_tipooperacional AND cmb.id_unidade = C.ucop_idoperacional 
  ORDER BY greg_id, uneg_id, muni_id, loca_id, unop_tipooperacional, unop_id;
  
  --INDICADOR 101 - Conformidade da demanda de potência elétrica contratada
  --ultrapassagem da demanda de potência elétrica medida em R$ / despesas totais com energia elétrica em R$
  INSERT INTO operacao.indicador_mensal
  SELECT cast(substring(cast(enel_referencia as text)  for 4) 
    || '-' || substring(cast(enel_referencia as text)  from 5 for 2)
