CREATE FUNCTION fib(fib_for integer) RETURNS integer
    LANGUAGE plpgsql
    AS $$
BEGIN
    IF fib_for < 2 THEN
       RETURN fib_for;
    END IF;
    RETURN fib(fib_for - 2) + fib(fib_for - 1);
   END;
$$;


