CREATE FUNCTION fixsequence() RETURNS smallint
    LANGUAGE plpgsql
    AS $$
declare
   v_reg record;
   v_sql varchar;
BEGIN for v_reg in (
                    select t.table_name
                    from information_schema.tables t
                    where t.table_schema = 'public'
      )
      loop
         v_sql := 'SELECT setval(''' || v_reg.table_name || '_id_seq'', 
                      (SELECT max(id)+1 FROM ' || v_reg.table_name ||'))';
