CREATE VIEW vw_imovel_principal_categoria AS
 SELECT imo.imov_id,
    ( SELECT sb.catg_id
           FROM subcategoria sb
          WHERE (sb.scat_id = ( SELECT imsb.scat_id
                   FROM imovel_subcategoria imsb
                  WHERE (imsb.imov_id = imo.imov_id)
                  ORDER BY imsb.imov_id, imsb.imsb_qteconomia DESC, imsb.scat_id
                 LIMIT 1))) AS catg_id
   FROM imovel imo;


